mapper.update:
update user
set mail_key=#{mail_key}
where user_email=#{user_email} and user_id=#{user_id}

update user
set mail_auth=1
where mail_auth=#{mail_auth} and user_email=#{user_email}

update user
set (mail_auth=1, user_cf=#{user_cf}, user_type=#{user_type})
where user_id=#{user_id}

update user
set phone_no=#{phone_no}
where user_id=#{user_id}

update user
set user_pw=#{user_pw}
where user_id=#{user_id}

update user
set mail_key=#{mail_key}
where user_id=#{user_id}

update user
set user_pw=#{user_pw}
where user_email=#{user_email}

mapper.select:
select mail_key 
from user 
where user_email = #{user_email}

mapper.update:
update user
set user_type=#{user_type}
where user_id=#{user_id}

update user
set user_cf=#{user_cf}
where user_id=#{user_id}
